import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { db, auth } from "./firebaseConfig"; // Asegúrate de importar auth y db
import { collection, getDocs, addDoc, Timestamp } from "firebase/firestore";
import { signOut } from "firebase/auth"; // Para cerrar sesión

const DeclaracionForm = () => {
  const [destinatario, setDestinatario] = useState("");
  const [cuerpo, setCuerpo] = useState("");
  const [publica, setPublica] = useState(true);
  const [usuarios, setUsuarios] = useState([]); // Estado para almacenar los usuarios registrados
  const [destinatarioManual, setDestinatarioManual] = useState(""); // Para ingresar manualmente el destinatario si se selecciona "OTRO"
  const navigate = useNavigate(); // Para la navegación
  const user = auth.currentUser; // Obtén el usuario autenticado

  // Consulta la colección de usuarios
  useEffect(() => {
    const fetchUsuarios = async () => {
      try {
        const querySnapshot = await getDocs(collection(db, "usuarios"));
        const usuariosData = [];
        querySnapshot.forEach((doc) => {
          usuariosData.push(doc.data().displayName); // Suponiendo que el nombre de usuario está en displayName
        });
        setUsuarios(usuariosData);
      } catch (error) {
        console.error("Error al obtener los usuarios:", error);
      }
    };

    fetchUsuarios();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const docRef = await addDoc(collection(db, "declaraciones"), {
        autor: user ? user.displayName : "Anónimo", // Si el usuario está autenticado, usa su nombre
        destinatario: destinatario === "OTRO" ? destinatarioManual : destinatario,
        publica,
        cuerpo,
        fecha: Timestamp.now(),
      });
      console.log("Declaración enviada con ID:", docRef.id);
      setDestinatario("");
      setDestinatarioManual("");
      setCuerpo("");
      setPublica(true);
      alert("Declaración enviada con éxito.");
    } catch (err) {
      console.error("Error al enviar la declaración:", err);
      alert("Ocurrió un error al enviar tu declaración.");
    }
  };

  const handleLogout = () => {
    signOut(auth)
      .then(() => {
        alert("Has cerrado sesión.");
        navigate("/login"); // Redirige al usuario a la página de login
      })
      .catch((error) => {
        console.error("Error al cerrar sesión:", error);
        alert("Ocurrió un error al cerrar sesión.");
      });
  };

  return (
    <div>
      {user ? (
        <form onSubmit={handleSubmit} className="declaracion-form">
          <h2>Hacer una declaración</h2>

          {/* Combobox para seleccionar destinatario */}
          <select
            value={destinatario}
            onChange={(e) => {
              setDestinatario(e.target.value);
              if (e.target.value !== "OTRO") {
                setDestinatarioManual(""); // Si no es "OTRO", limpiar el campo manual
              }
            }}
            required
          >
            <option value="">Selecciona un destinatario</option>
            {usuarios.map((usuario, index) => (
              <option key={index} value={usuario}>
                {usuario}
              </option>
            ))}
            <option value="OTRO">Otro</option>
          </select>

          {/* Si el destinatario es "OTRO", permitir ingresar manualmente el nombre */}
          {destinatario === "OTRO" && (
            <input
              type="text"
              placeholder="Escribe el nombre de tu crush"
              value={destinatarioManual}
              onChange={(e) => setDestinatarioManual(e.target.value)}
              required
            />
          )}

          <textarea
            placeholder="Escribe tu declaración"
            value={cuerpo}
            onChange={(e) => setCuerpo(e.target.value)}
            required
          ></textarea>

          <label>
            Pública:
            <input
              type="checkbox"
              checked={publica}
              onChange={(e) => setPublica(e.target.checked)}
            />
          </label>
          <button type="submit">Enviar</button>
          <button type="button" onClick={handleLogout}>
            Salir
          </button>
        </form>
      ) : (
        <div>
          <h2>Debes iniciar sesión para hacer una declaración.</h2>
          <button onClick={() => navigate("/login")}>Ir a Login</button>
        </div>
      )}
    </div>
  );
};

export default DeclaracionForm;
