import React, { useState } from "react";
import { auth } from "./firebaseConfig";
import { createUserWithEmailAndPassword, updateProfile } from "firebase/auth"; // Importamos updateProfile
/*import "./Register.css"; // Importa los estilos*/
import './styles.css';

function Register() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [userName, setUserName] = useState(""); // Agregamos estado para el nombre de usuario
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Primero creamos el usuario con email y contraseña
      const userCredential = await createUserWithEmailAndPassword(auth, email, password);
      const user = userCredential.user;

      // Luego, actualizamos el perfil del usuario con el nombre de usuario
      await updateProfile(user, {
        displayName: userName, // Asignamos el nombre de usuario al perfil
      });

      setEmail("");
      setPassword("");
      setUserName(""); // Limpiamos el campo de nombre de usuario
      setError("");
      alert("¡Usuario registrado exitosamente!");
    } catch (error) {
      setError(error.message);
    }
  };

  return (
    <div className="register-container">
      <div className="register-box">
        <h2>Registro de Usuario</h2>
        <form onSubmit={handleSubmit}>
          <div>
            <label>Nombre de Usuario:</label>
            <input
              type="text"
              value={userName}
              onChange={(e) => setUserName(e.target.value)} // Manejamos el estado del nombre de usuario
              required
            />
          </div>
          <div>
            <label>Email:</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div>
            <label>Contraseña:</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <button type="submit">Registrar</button>
        </form>
        {error && <p className="error-message">{error}</p>}
      </div>
    </div>
  );
}

export default Register;
