import React, { useState } from "react";
import { auth } from "./firebaseConfig"; // Asegúrate de importar la configuración de Firebase
import { createUserWithEmailAndPassword } from "firebase/auth"; // Método de Firebase para registrar usuarios

function Register() {
  // Definir el estado del formulario
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  // Manejar el envío del formulario
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Intenta crear un nuevo usuario con Firebase
      await createUserWithEmailAndPassword(auth, email, password);
      setEmail(""); // Limpiar el campo de email
      setPassword(""); // Limpiar el campo de contraseña
      setError(""); // Limpiar cualquier error previo
      alert("¡Usuario registrado exitosamente!");
    } catch (error) {
      // Si hay un error, mostrarlo
      setError(error.message);
    }
  };

  return (
    <div>
      <h2>Registro de Usuario</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Contraseña:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">Registrar</button>
      </form>
      {error && <p style={{ color: "red" }}>{error}</p>} {/* Mostrar errores si los hay */}
    </div>
  );
}

export default Register;
