import React, { useState } from "react";
import { agregarDeclaracion } from "./firestoreService";

const DeclaracionForm = ({ usuario }) => {
  const [destinatario, setDestinatario] = useState("");
  const [publica, setPublica] = useState(true);
  const [cuerpo, setCuerpo] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!cuerpo || !destinatario) {
      alert("Por favor completa todos los campos.");
      return;
    }
    await agregarDeclaracion(usuario, destinatario, publica, cuerpo);
    alert("Declaración enviada.");
    setDestinatario("");
    setCuerpo("");
    setPublica(true);
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Hacer una declaración</h2>
      <input
        type="text"
        placeholder="Destinatario"
        value={destinatario}
        onChange={(e) => setDestinatario(e.target.value)}
        required
      />
      <textarea
        placeholder="Escribe tu declaración"
        value={cuerpo}
        onChange={(e) => setCuerpo(e.target.value)}
        required
      ></textarea>
      <label>
        Pública:
        <input
          type="checkbox"
          checked={publica}
          onChange={(e) => setPublica(e.target.checked)}
        />
      </label>
      <button type="submit">Enviar</button>
    </form>
  );
};

export default DeclaracionForm;
