import React, { useState, useEffect } from "react";
import { getAuth, onAuthStateChanged } from "firebase/auth";
import DeclaracionForm from "./DeclaracionForm";
import Declaraciones from "./Declaraciones";
import Register from "./Register"; 
import Login from "./Login"; 

function App() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const auth = getAuth();
    const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser); // Actualiza el estado con el usuario autenticado o null
    });

    return () => unsubscribe(); // Limpia el listener al desmontar
  }, []);

  return (
    <div>
      <h1>ITLA Crush</h1>
      {/* Siempre muestra las declaraciones públicas y anónimas */}
      <Declaraciones />

      {/* Muestra opciones adicionales según el estado de autenticación */}
      {!user ? (
        <>
          <h2>Inicia sesión o regístrate para más funciones</h2>
          <Login /> {/* Componente de inicio de sesión */}
          <Register /> {/* Componente de registro */}
        </>
      ) : (
        <>
          <h2>Bienvenido, {user.displayName || "Usuario"}</h2>
          <DeclaracionForm /> {/* Componente del formulario de declaraciones */}
        </>
      )}
    </div>
  );
}

export default App;
