import React, { useState } from "react";
import { db } from "./firebaseConfig"; // Asegúrate de que esto apunta a tu configuración de Firebase
import { collection, addDoc, Timestamp } from "firebase/firestore";
import { getAuth, signOut } from "firebase/auth";
import { useNavigate } from "react-router-dom"; // Para redirigir a otra página

const DeclaracionForm = () => {
  const [destinatario, setDestinatario] = useState("");
  const [cuerpo, setCuerpo] = useState("");
  const [publica, setPublica] = useState(true);

  // Obtén el usuario actual
  const auth = getAuth();
  const usuario = auth.currentUser;
  
  // Redirección usando useNavigate de react-router-dom
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (!usuario) {
        alert("Debes estar logueado para hacer una declaración.");
        return;
      }

      const docRef = await addDoc(collection(db, "declaraciones"), {
        autor: usuario.displayName || "Anónimo",
        destinatario: destinatario || "Otro",
        publica,
        cuerpo,
        fecha: Timestamp.now(),
      });

      console.log("Declaración enviada con ID:", docRef.id);
      setDestinatario("");
      setCuerpo("");
      setPublica(true);
      alert("Declaración enviada con éxito.");
    } catch (err) {
      console.error("Error al enviar la declaración:", err);
      alert("Ocurrió un error al enviar tu declaración.");
    }
  };

  const handleLogout = async () => {
    try {
      await signOut(auth);
      alert("Sesión cerrada con éxito.");
      navigate("/login"); // Redirige a la página de login después de cerrar sesión
    } catch (err) {
      console.error("Error al cerrar sesión:", err);
      alert("Ocurrió un error al cerrar sesión.");
    }
  };

  return (
    <form onSubmit={handleSubmit} className="declaracion-form">
      <h2>Hacer una declaración</h2>
      <input
        type="text"
        placeholder="Destinatario"
        value={destinatario}
        onChange={(e) => setDestinatario(e.target.value)}
        required
      />
      <textarea
        placeholder="Escribe tu declaración"
        value={cuerpo}
        onChange={(e) => setCuerpo(e.target.value)}
        required
      ></textarea>
      <label>
        Pública:
        <input
          type="checkbox"
          checked={publica}
          onChange={(e) => setPublica(e.target.checked)}
        />
      </label>
      <button type="submit">Enviar</button>
      {usuario && (
        <button type="button" onClick={handleLogout}>
          Salir
        </button>
      )}
    </form>
  );
};

export default DeclaracionForm;
