import { useEffect, useState } from "react";
import { db } from "./firebaseConfig"; // Asegúrate de importar db desde firebaseConfig
import { collection, addDoc } from "firebase/firestore"; 

const DeclaracionForm = () => {
  const [usuarios, setUsuarios] = useState([]);
  const [destinatario, setDestinatario] = useState("");
  const [destinatarioManual, setDestinatarioManual] = useState("");
  const [cuerpo, setCuerpo] = useState("");
  const [publica, setPublica] = useState(true);

  // Cargar usuarios desde Firestore
  useEffect(() => {
    const fetchUsuarios = async () => {
      try {
        const querySnapshot = await getDocs(collection(db, "usuarios"));
        const usuariosData = [];
        querySnapshot.forEach((doc) => {
          usuariosData.push(doc.data().nombre); // Cambia "nombre" por el campo que quieras usar
        });
        setUsuarios(usuariosData);
      } catch (error) {
        console.error("Error al obtener los usuarios: ", error);
      }
    };
    fetchUsuarios();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Si el destinatario es "OTRO", usamos el nombre manual
    const destinatarioFinal = destinatario === "OTRO" ? destinatarioManual : destinatario;

    if (!destinatarioFinal || !cuerpo) {
      alert("El destinatario y el cuerpo de la declaración son obligatorios.");
      return;
    }

    try {
      // Guardar la declaración en Firestore
      await addDoc(collection(db, "declaraciones"), {
        destinatario: destinatarioFinal,
        cuerpo: cuerpo,
        publica: publica,
        fecha: new Date(),
      });

      // Limpiar el formulario después de enviar
      setDestinatario("");
      setDestinatarioManual("");
      setCuerpo("");
      setPublica(true);

      alert("Tu declaración ha sido enviada.");
    } catch (error) {
      console.error("Error al enviar la declaración: ", error);
      alert("Ocurrió un error al enviar tu declaración.");
    }
  };

  return (
    <form onSubmit={handleSubmit} className="declaracion-form">
      <h2>Hacer una declaración</h2>

      {/* Combobox para seleccionar destinatario */}
      <select
        value={destinatario}
        onChange={(e) => {
          setDestinatario(e.target.value);
          if (e.target.value !== "OTRO") {
            setDestinatarioManual(""); // Limpiar el campo si no es "OTRO"
          }
        }}
        required
      >
        <option value="">Selecciona un destinatario</option>
        {usuarios.map((usuario, index) => (
          <option key={index} value={usuario}>
            {usuario}
          </option>
        ))}
        <option value="OTRO">Otro</option>
      </select>

      {/* Si el destinatario es "OTRO", mostrar el campo para ingresar manualmente */}
      {destinatario === "OTRO" && (
        <input
          type="text"
          placeholder="Escribe el nombre de tu crush"
          value={destinatarioManual}
          onChange={(e) => setDestinatarioManual(e.target.value)}
          required
          className="manual-destinatario"
        />
      )}

      <textarea
        placeholder="Escribe tu declaración"
        value={cuerpo}
        onChange={(e) => setCuerpo(e.target.value)}
        required
      ></textarea>

      <label>
        Pública:
        <input
          type="checkbox"
          checked={publica}
          onChange={(e) => setPublica(e.target.checked)}
        />
      </label>

      <button type="submit">Enviar</button>
    </form>
  );
};

export default DeclaracionForm;
