import React, { useState } from "react";
import { db } from "./firebaseConfig"; // Asegúrate de que esto apunta a tu configuración de Firebase
import { collection, addDoc, Timestamp } from "firebase/firestore";
import './styles.css';

const DeclaracionForm = () => {
  const [destinatario, setDestinatario] = useState("");
  const [cuerpo, setCuerpo] = useState("");
  const [publica, setPublica] = useState(true);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const docRef = await addDoc(collection(db, "declaraciones"), {
        autor: "Anónimo", // Cambia esto por el nombre de usuario autenticado, si es necesario
        destinatario: destinatario || "Otro",
        publica,
        cuerpo,
        fecha: Timestamp.now(),
      });
      console.log("Declaración enviada con ID:", docRef.id);
      setDestinatario("");
      setCuerpo("");
      setPublica(true);
      alert("Declaración enviada con éxito.");
    } catch (err) {
      console.error("Error al enviar la declaración:", err);
      alert("Ocurrió un error al enviar tu declaración.");
    }
  };

  return (
    <form onSubmit={handleSubmit} className="declaracion-form">
      <h2>Hacer una declaración</h2>
      <input
        type="text"
        placeholder="Destinatario"
        value={destinatario}
        onChange={(e) => setDestinatario(e.target.value)}
        required
      />
      <textarea
        placeholder="Escribe tu declaración"
        value={cuerpo}
        onChange={(e) => setCuerpo(e.target.value)}
        required
      ></textarea>
      <label>
        Pública:
        <input
          type="checkbox"
          checked={publica}
          onChange={(e) => setPublica(e.target.checked)}
        />
      </label>
      <button type="submit">Enviar</button>
    </form>
  );
};

export default DeclaracionForm;
