import React, { useState } from "react";
import './styles.css'; // Importar archivo CSS para los estilos

const Declaraciones = () => {
  const [autor, setAutor] = useState(""); // Autor del mensaje
  const [destinatario, setDestinatario] = useState(""); // Destinatario
  const [cuerpo, setCuerpo] = useState(""); // Cuerpo del mensaje
  const [publica, setPublica] = useState(false); // Estado para el checkbox de público
  const [anonimo, setAnonimo] = useState(false); // Estado para el checkbox de anónimo
  const [mensajeError, setMensajeError] = useState(""); // Mensaje de error si no se selecciona ningún checkbox

  // Función para manejar el envío del mensaje
  const handleEnviar = () => {
    if (!publica && !anonimo) {
      // Mostrar error si no se selecciona ningún checkbox
      setMensajeError("¡Debes seleccionar al menos un checkbox: 'Público' o 'Anónimo'!");
      return;
    }
    
    // Aquí va la lógica para guardar el mensaje (con Firebase o lo que sea necesario)
    // Ejemplo de declaración:
    const nuevaDeclaracion = {
      autor: anonimo ? "Anónimo" : autor, // Si es anónimo, asignar "Anónimo" como autor
      destinatario,
      cuerpo,
      publica,
      anonimo,
    };
    console.log(nuevaDeclaracion); // Mostrar para depuración

    // Resetear campos después de enviar
    setAutor("");
    setDestinatario("");
    setCuerpo("");
    setPublica(false);
    setAnonimo(false);
    setMensajeError(""); // Limpiar mensaje de error
  };

  return (
    <div>
      <h1>Publicar Declaración</h1>
      {mensajeError && <p className="error-message">{mensajeError}</p>} {/* Mostrar mensaje de error si existe */}

      <div>
        <label>
          Autor:
          <input
            type="text"
            value={autor}
            onChange={(e) => setAutor(e.target.value)}
            placeholder="Nombre del autor"
          />
        </label>
      </div>

      <div>
        <label>
          Destinatario:
          <input
            type="text"
            value={destinatario}
            onChange={(e) => setDestinatario(e.target.value)}
            placeholder="Nombre del destinatario"
          />
        </label>
      </div>

      <div>
        <label>
          Cuerpo del mensaje:
          <textarea
            value={cuerpo}
            onChange={(e) => setCuerpo(e.target.value)}
            placeholder="Escribe tu mensaje"
          />
        </label>
      </div>

      <div>
        <label>
          <input
            type="checkbox"
            checked={publica}
            onChange={() => setPublica(!publica)}
          />
          Público
        </label>
        <label>
          <input
            type="checkbox"
            checked={anonimo}
            onChange={() => setAnonimo(!anonimo)}
          />
          Anónimo
        </label>
      </div>

      <button onClick={handleEnviar}>Publicar</button>
    </div>
  );
};

export default Declaraciones;
