import React, { useState, useEffect } from "react";
import { getAuth, onAuthStateChanged } from "firebase/auth"; // Nueva API modular
import DeclaracionForm from "./DeclaracionForm";
import Declaraciones from "./Declaraciones";
import Register from "./Register"; // Componente de registro
import Login from "./Login"; // Componente de inicio de sesión

function App() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(getAuth(), setUser); // Usamos la nueva forma de obtener el auth
    return () => unsubscribe();
  }, []);

  return (
    <div>
      <h1>ITLA Crush</h1>
      {!user ? (
        <>
          <Login /> {/* Muestra Login si no está logueado */}
          <Register /> {/* Muestra Register si no está logueado */}
        </>
      ) : (
        <>
          <DeclaracionForm /> {/* Mostrar el formulario de declaración si está logueado */}
          <Declaraciones /> {/* Mostrar las declaraciones */}
        </>
      )}
    </div>
  );
}

export default App;
