import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { db } from "./firebaseConfig"; // Asegúrate de que esto apunta a tu configuración de Firebase
import { collection, addDoc, Timestamp, getDocs } from "firebase/firestore";
import { auth, signOut } from "./firebaseConfig"; // Asegúrate de tener la referencia a Firebase Auth

const DeclaracionForm = () => {
  const [destinatario, setDestinatario] = useState("");
  const [cuerpo, setCuerpo] = useState("");
  const [publica, setPublica] = useState(true);
  const [usuarios, setUsuarios] = useState([]); // Para almacenar los usuarios registrados
  const [otroDestinatario, setOtroDestinatario] = useState(""); // Campo para el destinatario si es "Otro"
  const [selectedDestinatario, setSelectedDestinatario] = useState(""); // Estado para el destinatario seleccionado
  const navigate = useNavigate(); // Para la navegación
  const user = auth.currentUser; // Obtén el usuario autenticado

  useEffect(() => {
    // Obtén los usuarios registrados de la base de datos
    const fetchUsuarios = async () => {
      const querySnapshot = await getDocs(collection(db, "usuarios"));
      const usuariosList = querySnapshot.docs.map(doc => doc.data().displayName);
      setUsuarios(usuariosList);
    };

    fetchUsuarios(); // Llama a la función al montar el componente

  }, []); // Solo se ejecuta una vez al montar el componente

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!user) {
      alert("Debes iniciar sesión para enviar una declaración.");
      return;
    }

    try {
      const docRef = await addDoc(collection(db, "declaraciones"), {
        autor: user.displayName || "Anónimo", // Si el usuario está autenticado, usa su nombre
        destinatario: selectedDestinatario === "Otro" ? otroDestinatario : selectedDestinatario, // Usa el destinatario manual si se selecciona "Otro"
        publica,
        cuerpo,
        fecha: Timestamp.now(),
      });
      console.log("Declaración enviada con ID:", docRef.id);
      setDestinatario("");
      setCuerpo("");
      setPublica(true);
      setSelectedDestinatario(""); // Reinicia el destinatario seleccionado
      setOtroDestinatario(""); // Reinicia el campo de destinatario manual
      alert("Declaración enviada con éxito.");
      navigate("/declaraciones"); // Redirige a la lista de declaraciones después de enviar
    } catch (err) {
      console.error("Error al enviar la declaración:", err);
      alert("Ocurrió un error al enviar tu declaración.");
    }
  };

  const handleLogout = () => {
    signOut(auth)
      .then(() => {
        alert("Has cerrado sesión.");
        navigate("/login"); // Redirige al usuario a la página de login
      })
      .catch((error) => {
        console.error("Error al cerrar sesión:", error);
        alert("Ocurrió un error al cerrar sesión.");
      });
  };

  return (
    <div>
      {user ? (
        <form onSubmit={handleSubmit} className="declaracion-form">
          <h2>Hacer una declaración</h2>
          
          {/* Combobox de destinatarios */}
          <label>Destinatario:</label>
          <select
            value={selectedDestinatario}
            onChange={(e) => setSelectedDestinatario(e.target.value)}
            required
          >
            <option value="">Selecciona un destinatario</option>
            {usuarios.map((usuario, index) => (
              <option key={index} value={usuario}>
                {usuario}
              </option>
            ))}
            <option value="Otro">Otro</option>
          </select>

          {/* Campo para introducir destinatario manual si se selecciona "Otro" */}
          {selectedDestinatario === "Otro" && (
            <input
              type="text"
              placeholder="Escribe el nombre de tu crush"
              value={otroDestinatario}
              onChange={(e) => setOtroDestinatario(e.target.value)}
              required
            />
          )}

          <textarea
            placeholder="Escribe tu declaración"
            value={cuerpo}
            onChange={(e) => setCuerpo(e.target.value)}
            required
          ></textarea>

          {/* Declaración pública */}
          <label>
            Pública:
            <input
              type="checkbox"
              checked={publica}
              onChange={(e) => setPublica(e.target.checked)}
            />
          </label>

          <button type="submit">Enviar</button>
          <button type="button" onClick={handleLogout}>
            Salir
          </button>
        </form>
      ) : (
        <div>
          <h2>Debes iniciar sesión para hacer una declaración.</h2>
          <button onClick={() => navigate("/login")}>Ir a Login</button>
        </div>
      )}
    </div>
  );
};

export default DeclaracionForm;
