import React, { useEffect, useState } from "react";
import { db } from "./firebaseConfig"; // Asegúrate de que esto apunta a tu configuración de Firebase
import { collection, query, where, onSnapshot } from "firebase/firestore";
import './styles.css';

const Declaraciones = () => {
  const [declaracionesPublicas, setDeclaracionesPublicas] = useState([]);

  useEffect(() => {
    // Esta función escuchará los cambios en tiempo real
    const q = query(
      collection(db, "declaraciones"),
      where("publica", "==", true)
    );

    // Usamos onSnapshot para escuchar en tiempo real
    const unsubscribe = onSnapshot(q, (querySnapshot) => {
      const declaraciones = [];
      querySnapshot.forEach((doc) => {
        declaraciones.push(doc.data());
      });
      setDeclaracionesPublicas(declaraciones);
    });

    // Cleanup cuando el componente se desmonte
    return () => unsubscribe();

  }, []); // Solo se ejecuta una vez al montar el componente

  return (
    <div>
      <h2>Declaraciones Públicas</h2>
      <ul>
        {declaracionesPublicas.map((declaracion, index) => (
          <li key={index}>
            <strong>{declaracion.autor || "Anónimo"}:</strong>{" "}
            {declaracion.cuerpo}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Declaraciones;
