import React, { useEffect, useState } from "react";
import { db } from "./firebaseConfig"; // Asegúrate de que esto apunta a tu configuración de Firebase
import { collection, query, where, onSnapshot } from "firebase/firestore";
import './styles.css';

const Declaraciones = () => {
  const [declaracionesPublicas, setDeclaracionesPublicas] = useState([]);
  const [filtroUsuario, setFiltroUsuario] = useState(""); // Estado para almacenar el filtro por usuario

  useEffect(() => {
    const q = query(
      collection(db, "declaraciones"),
      where("publica", "in", [true, false]) // Incluir tanto las públicas como las no públicas
    );

    // Escuchar los cambios en tiempo real
    const unsubscribe = onSnapshot(q, (querySnapshot) => {
      const declaraciones = [];
      querySnapshot.forEach((doc) => {
        declaraciones.push(doc.data());
      });
      setDeclaracionesPublicas(declaraciones);
    });

    // Limpiar el listener cuando el componente se desmonte
    return () => unsubscribe();
  }, []);

  // Filtrar las declaraciones por el nombre de usuario
  const declaracionesFiltradas = declaracionesPublicas.filter((declaracion) => {
    return declaracion.autor
      ? declaracion.autor.toLowerCase().includes(filtroUsuario.toLowerCase())
      : true; // Si no hay autor, no lo filtra
  });

  return (
    <div>
      <h2>Declaraciones Públicas</h2>
      
      {/* Campo de filtro para el nombre de usuario */}
      <input
        type="text"
        placeholder="Filtrar por nombre de usuario"
        value={filtroUsuario}
        onChange={(e) => setFiltroUsuario(e.target.value)}
      />

      <div className="declaraciones-container">
        <ul>
          {declaracionesFiltradas.map((declaracion, index) => (
            <li key={index}>
              <strong>{declaracion.publica ? declaracion.autor || "Anónimo" : "Anónimo"}:</strong>{" "}
              {declaracion.cuerpo}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Declaraciones;
