import React from "react";
import DeclaracionForm from "./DeclaracionForm";  // Importa el formulario para crear declaraciones
import Declaraciones from "./Declaraciones";      // Importa el componente para mostrar las declaraciones públicas
import './App.css';  // Asegúrate de que tus estilos están aplicados correctamente

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <h1>Bienvenido a ITLA Crush</h1>
        <p>Un lugar donde las declaraciones de amor se hacen realidad</p>
      </header>
      <main>
        <DeclaracionForm /> {/* Formulario para hacer una declaración de amor */}
        <Declaraciones />   {/* Lista de declaraciones públicas */}
      </main>
    </div>
  );
}

export default App;
