import React, { useEffect, useState } from "react";
import { obtenerDeclaracionesPublicas, obtenerDeclaracionesPrivadas } from "./firestoreService";

const Declaraciones = ({ usuario }) => {
  const [declaracionesPublicas, setDeclaracionesPublicas] = useState([]);
  const [declaracionesPrivadas, setDeclaracionesPrivadas] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const publicas = await obtenerDeclaracionesPublicas();
      setDeclaracionesPublicas(publicas);

      if (usuario) {
        const privadas = await obtenerDeclaracionesPrivadas(usuario);
        setDeclaracionesPrivadas(privadas);
      }
    };
    fetchData();
  }, [usuario]);

  return (
    <div>
      <h2>Declaraciones Públicas</h2>
      <ul>
        {declaracionesPublicas.map((declaracion, index) => (
          <li key={index}>
            <strong>{declaracion.autor || "Anónimo"}:</strong> {declaracion.cuerpo}
          </li>
        ))}
      </ul>
      {usuario && (
        <>
          <h2>Declaraciones Privadas</h2>
          <ul>
            {declaracionesPrivadas.map((declaracion, index) => (
              <li key={index}>
                <strong>{declaracion.autor}:</strong> {declaracion.cuerpo}
              </li>
            ))}
          </ul>
        </>
      )}
    </div>
  );
};

export default Declaraciones;
